css:
.main {
            margin-left: 3.5mm;
            margin-right: 0.7mm;
            display: grid;
            grid-template-columns: repeat(2, 50%);
           padding-bottom:3px;

            height: auto; /* Allow the height to adjust based on content */
        }

html:
{% set serial_no_list = frappe.db.get_all('Serial No',filters = {'purchase_document_no':doc.name}, fields = ["name", "item_code"]) %}
<div class='main'>
{% for i in serial_no_list %}
{% set serial_item_code = frappe.db.get_value("Purchase Receipt Item", {"item_code": i.item_code}, ["item_code"]) %}
{% set serial_mrp = frappe.db.get_value("Purchase Receipt Item", {"item_code": i.item_code}, ["custom_mrp"]) %}
<div>
        <center>{{ serial_item_code or ""}}</center>
        <center>{{ serial_mrp or 0.00}}</center>
        <center><img src="https://barcode.tec-it.com/barcode.ashx?data={{(i.name)}}&code=Code128&translate-esc=true" width="200px" height="60px"></center>
        <!--<center><img src='data:image/png;base64,{{get_serialno_barcode(i.name) }}' style="width: 6cm; height: 2cm;" ></center>-->
</div>
{% endfor %}
</div>
